import React, {useState} from 'react';
import './App.scss';

const quotesJson = "https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json"
function App() {
    const [quote, setQuote] = useState('ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum ');
    const [author, setAuthor] = useState('Yo jimbo');
    const [ramdonNumber, setRamdonNumber] = useState(0);
    const [quotesList, setQuotesList] = useState(null);

    useEffect(() => {
      const fetchQuotes = async (quotesJson) => {
          const response = await fetch(quotesJson)
          const parsec = response.json()
          setQuotesList(parsec)
      }
    }, [quotesJson]);


    const generateRandomQuote = () => {
        let rng = Math.round(quotes.length*Math.random());
         setRamdonNumber(rng);
         setQuote(quotes[rng].quote)
         setAuthor(quotes[rng].author)
    }

    const quotes = [{quote: "lorem", author: "Yojimbo"}, {quote: "asdadasd", author: "qweqeq"},{quote: "2131231", author: "sdadads"},{quote: "asdadsads", author: "sdada2131ds"},{quote: "21312ssasda31", author: "sdad32123ads"}];

  return (   
    <div className="container">
        <h1>numnber {ramdonNumber}</h1>
        <div className='quote-box'>
            <p>
                "{quote}"
            </p>
            <p>
                - {author}
            </p>
        <button onClick={()=>generateRandomQuote()}>Generate Number Random</button> 
        </div>
    </div>
  );
}

export default App;
