import React, {useState} from 'react';
import './App.scss';
function App() {
    const [quote, setQuote] = useState('ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum ');
    const [author, setAuthor] = useState('Yo jimbo');
    const refreshQuote = () => {
        setQuote("1");
        setAuthor("2");
    };
    const [ramdonNumber, setRamdonNumber] = useState(0);
    const generateRamdonNumber = () => {
        let rng = Math.round(3*Math.random());
         setRamdonNumber(rng);
         if (rng === 0) {
             setQuote(quotes[0])
         }
         if (rng === 1) {
             setQuote(quotes[1])
         }
         if (rng ===2) {
             setQuote(quotes[2])
         }
    }
    const quotes = ["lorem", "Inpus" , "marzo"];

  return (   
    <div className="container">
        <h1>Random Number {ramdonNumber}</h1>
        <button onClick={()=>generateRamdonNumber()}>Generate Number Random</button> 
        <div className='quote-box'>
            <p>
                "{quote}"
            </p>
            <p>
                - {author}
            </p>
            <button onClick={refreshQuote}>Change Quote</button>
        </div>
    </div>
  );
}

export default App;
