import React, {useEffect, useState} from 'react';
import './App.scss';

const quotesJson = "https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json"
function App() {
    const [quote, setQuote] = useState('ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum ');
    const [author, setAuthor] = useState('Yo jimbo');
    const [ramdonNumber, setRamdonNumber] = useState(0);
    const [quotesList, setQuotesList] = useState(null);

    const fetchQuotes = async (url) => {
        const response = await fetch(url)
        const parsedList = await response.json()
        setQuotesList(parsedList.quotes)
    }
    useEffect(() => {
            fetchQuotes(quotesJson)
    });


    const generateRandomQuote = () => {
        let rng = Math.round(quotesList.length*Math.random());
         setRamdonNumber(rng);
         setQuote(quotesList[rng].quote)
         setAuthor(quotesList[rng].author)
    }

  return (   
    <div className="container">
        <div id="quote-box" className='quote_box'>
            <p id="text" className='quote__text'>
                "{quote}"
            </p>
            <p id='author' className='quote__author'>
                - {author}
            </p>
        <button id='new-quote' onClick={()=>generateRandomQuote()}>Generate Number Random</button>
        <a id="tweet-quote" rel="noreferrer" 
        href={encodeURI(`http://www.twitter.com/intent/tweet?text=${quote}\n- ${author}`)} target={"_blank"}>Tweet Quote</a>
        </div>
    </div>
  );
}

export default App;
